mapper.update:
UPDATE isv_corp_staff
SET
    gmt_modified = now(),
    union_id     = #{unionId}
WHERE corp_id = #{corpId} AND user_id = #{userId}

UPDATE isv_corp_staff
SET
    gmt_modified = now(),
    is_admin     = #{isAdmin},
    sys_level    = #{sysLevel}
WHERE corp_id = #{corpId} AND user_id = #{userId}

